mapper.select:
SELECT MAX(code) FROM department

	company_id = #{companyId}

        AND code LIKE CONCAT(#{prefix}, '%')


    	AND code LIKE #{prefix} || '%'


        AND code LIKE #{prefix} || '%'


        AND code LIKE #{prefix} || '%'


        AND code LIKE #{prefix} || '%'


        AND code LIKE #{prefix} + '%'


    	AND LENGTH(code) = #{digit}


    	AND LENGTH(code) = #{digit}


        AND LENGTH(code) = #{digit}


        AND LENGTH(code) = #{digit}


        AND LENGTH(code) = #{digit}


        AND LENGTH(code) = #{digit}
